import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Select,
  MenuItem,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  Grid,
  Paper,
  Tabs,
  Tab,
  CircularProgress,
} from '@mui/material';

const DESCRIPTION_PREVIEW_LENGTH = 150;

const categories = [
  'Technology',
  'Food & Beverage',
  'Environmental',
  'Healthcare',
  'Finance',
  'Retail',
  'Education',
  'Manufacturing',
  'Energy',
  'Transportation',
];

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterSize, setFilterSize] = useState('');
  const [filterLocation, setFilterLocation] = useState('');
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [companiesData, setCompaniesData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [allCompaniesLoaded, setAllCompaniesLoaded] = useState(false);
  const [offset, setOffset] = useState(0);
  const [limit, setLimit] = useState(10);
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    fetchCompanies();
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [searchTerm, filterCategory, filterSize, filterLocation]);

  const filteredCompanies = companiesData.filter((company) => {
    const nameMatch = company.name.toLowerCase().includes(searchTerm.toLowerCase());
    let categoryMatch = true;
    let sizeMatch = true;
    let locationMatch = true;

    if (filterCategory) {
      categoryMatch = company.category === filterCategory;
    }
    if (filterSize) {
      sizeMatch = company.size === filterSize;
    }
    if (filterLocation) {
      locationMatch = company.location === filterLocation;
    }

    return nameMatch && categoryMatch && sizeMatch && locationMatch;
  });

  const fetchCompanies = async () => {
    if (allCompaniesLoaded) return;
    setLoading(true);
    try {
      let url = `http://127.0.0.1:8000/companies?limit=${limit}&offset=${offset}`; // Correct URL

      if (filterCategory) url += `&category=${filterCategory}`;
      if (filterSize) url += `&size=${filterSize}`;
      if (filterLocation) url += `&location=${filterLocation}`;
      if (searchTerm) url += `&search=${searchTerm}`;

      const response = await fetch(url);
      const data = await response.json();

      if (data.length === 0) {
        setAllCompaniesLoaded(true);
      } else {
        setCompaniesData((prevData) => [...prevData, ...data]);
        setOffset((prevOffset) => prevOffset + limit);
      }
    } catch (error) {
      console.error('Error fetching companies:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleScroll = () => {
    if (
      window.innerHeight + window.scrollY >=
      document.body.offsetHeight - 200 &&
      !loading &&
      !allCompaniesLoaded
    ) {
      fetchCompanies();
    }
  };

  const handleCompanyClick = (company) => {
    setSelectedCompany(company);
  };

  const handleBack = () => {
    setSelectedCompany(null);
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  if (selectedCompany) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Button variant="outlined" onClick={handleBack} sx={{ mb: 2 }}>
          Back to Directory
        </Button>
        <Paper elevation={3} sx={{ p: 3, borderRadius: 2 }}>
          {/* Hero Section (Logo and Name) - Moved outside tabs */}
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              mb: 3,
              textAlign: 'center',
            }}
          >
            <CardMedia
              component="img"
              sx={{ width: 150, height: 150, objectFit: 'contain', mb: 1 }}
              image={selectedCompany.logo}
              alt={selectedCompany.name}
            />
            <Typography variant="h3" component="div">
              {selectedCompany.name}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Innovating for a Better Future
            </Typography>
            <Button
              href={selectedCompany.website}
              target="_blank"
              rel="noopener noreferrer"
              variant="contained"
              sx={{ mt: 2 }}
            >
              Visit Our Website
            </Button>
          </Box>

          <Tabs value={activeTab} onChange={handleTabChange} aria-label="company tabs">
            <Tab label="About Us" />
            <Tab label="Porfolio" />
            <Tab label="Investors" />
            <Tab label="Assessment" />
            <Tab label="Transformation Plan" />
          </Tabs>

          {activeTab === 0 && (
            <>
              <Typography variant="h5" component="div" sx={{ mt: 3, mb: 1 }}>
                About Us
              </Typography>
              <Typography variant="body1" sx={{ mb: 2 }}>
                {selectedCompany.description}
              </Typography>
              <Typography variant="body2" sx={{ mb: 2 }}>
                Key Features:
                <ul>
                  <li>Cutting-edge solutions</li>
                  <li>Dedicated team</li>
                  <li>Sustainable practices</li>
                </ul>
              </Typography>

              {/* Key Information Section (Moved inside About Us tab) */}
              <Typography variant="h5" component="div" sx={{ mt: 3, mb: 1 }}>
                Key Information
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Typography variant="body1" sx={{ ml: 1 }}>
                      Category: {selectedCompany.category}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
