import React, { useState } from 'react';
import {
  Container,
  TextField,
  Select,
  MenuItem,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  Grid,
} from '@mui/material';

// Dummy data (replace with your data)
const companiesData = [
  {
    id: 1,
    name: 'Tech Innovations Inc.',
    category: 'Technology',
    size: 'Large',
    location: 'San Francisco, CA',
    employees: 500,
    description: 'Leading tech company specializing in AI.',
    logo: '/logos/tech.png', // Random tech image
    website: 'https://techinnovations.com',
  },
  {
    id: 2,
    name: 'Foodie Delights Ltd.',
    category: 'Food & Beverage',
    size: 'Medium',
    location: 'New York, NY',
    employees: 150,
    description: 'Delicious food and catering services.',
    logo: 'https://source.unsplash.com/random/200x100/?food', // Random food image
    website: 'https://foodiedelights.com',
  },
  {
    id: 3,
    name: 'Green Solutions Co.',
    category: 'Environmental',
    size: 'Small',
    location: 'London, UK',
    employees: 50,
    description: 'Sustainable environmental solutions.',
    logo: 'https://source.unsplash.com/random/200x100/?environment', // Random env image
    website: 'https://greensolutions.co.uk',
  },
  // ... more companies
];

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterSize, setFilterSize] = useState('');
  const [filterLocation, setFilterLocation] = useState('');
  const [selectedCompany, setSelectedCompany] = useState(null);

  const filteredCompanies = companiesData.filter((company) => {
    const nameMatch = company.name.toLowerCase().includes(searchTerm.toLowerCase());
    const categoryMatch = filterCategory ? company.category === filterCategory : true;
    const sizeMatch = filterSize ? company.size === filterSize : true;
    const locationMatch = filterLocation ? company.location === filterLocation : true;
    return nameMatch && categoryMatch && sizeMatch && locationMatch;
  });

  const handleCompanyClick = (company) => {
    setSelectedCompany(company);
  };

  const handleBack = () => {
    setSelectedCompany(null);
  };

  if (selectedCompany) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Button variant="outlined" onClick={handleBack} sx={{ mb: 2 }}>
          Back to Directory
        </Button>
        <Card>
          <CardMedia component="img" height="200" image={selectedCompany.logo} alt={selectedCompany.name} />
          <CardContent>
            <Typography variant="h5" component="div">
              {selectedCompany.name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Category: {selectedCompany.category}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Size: {selectedCompany.size}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Location: {selectedCompany.location}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Employees: {selectedCompany.employees}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Description: {selectedCompany.description}
            </Typography>
            <Button href={selectedCompany.website} target="_blank" rel="noopener noreferrer" sx={{ mt: 2 }}>
              Visit Website
            </Button>
          </CardContent>
        </Card>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Company Directory
      </Typography>

      <Box sx={{ mb: 3 }}>
        <TextField
          label="Search companies..."
          variant="outlined"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ mb: 1 }}
        />
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6} md={3}>
            <Select fullWidth value={filterCategory} onChange={(e) => setFilterCategory(e.target.value)}>
              <MenuItem value="">All Categories</MenuItem>
              <MenuItem value="Technology">Technology</MenuItem>
              <MenuItem value="Food & Beverage">Food & Beverage</MenuItem>
              <MenuItem value="Environmental">Environmental</MenuItem>
            </Select>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Select fullWidth value={filterSize} onChange={(e) => setFilterSize(e.target.value)}>
              <MenuItem value="">All Sizes</MenuItem>
              <MenuItem value="Small">Small</MenuItem>
              <MenuItem value="Medium">Medium</MenuItem>
              <MenuItem value="Large">Large</MenuItem>
            </Select>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Select fullWidth value={filterLocation} onChange={(e) => setFilterLocation(e.target.value)}>
              <MenuItem value="">All Locations</MenuItem>
              <MenuItem value="San Francisco, CA">San Francisco, CA</MenuItem>
              <MenuItem value="New York, NY">New York, NY</MenuItem>
              <MenuItem value="London, UK">London, UK</MenuItem>
            </Select>
          </Grid>
        </Grid>
      </Box>

      <Grid container spacing={3}>
        {filteredCompanies.map((company) => (
          <Grid item xs={12} sm={6} md={4} key={company.id}>
            <Card onClick={() => handleCompanyClick(company)} sx={{ cursor: 'pointer', height: '100%' }}>
              <CardMedia component="img" height="140" image={company.logo} alt={company.name} />
              <CardContent>
                <Typography variant="h6" component="div">
                  {company.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Category: {company.category}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Location: {company.location}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default App;
